  "keywords": "design-system, buttons",
  "symbols": "BtnGroup, BtnGroup-form, BtnGroup-item, BtnGroup-parent, Counter, btn, btn-block, btn-blue, btn-danger, btn-large, btn-link, btn-outline, btn-primary, btn-purple, btn-sm, btn-with-count, details-reset, disabled, ellipsis-expander, focus, hidden-text-expander, hover, inline, octicon, selected, social-count",
